mapper.select:
SELECT s.SALE_ID, s.CUSTOMER_ID, s.PRODUCT_ID, s.QUANTITY, s.UNIT_PRICE, s.SALE_AT, s.CREATED_AT, s.UPDATED_AT, c.COMPANY_NAME as customerName, p.PRODUCT_NAME as productName, p.SALE_PRICE as productPrice FROM SALES s JOIN CUSTOMERS c ON s.CUSTOMER_ID = c.CUSTOMER_ID JOIN PRODUCTS p ON s.PRODUCT_ID = p.PRODUCT_ID WHERE NVL(s.DELETED, 0) = 0 AND NVL(c.DELETED, 0) = 0 AND NVL(p.DELETED, 0) = 0
AND ( LOWER(c.COMPANY_NAME) LIKE LOWER('%'||#{searchQuery}||'%') OR LOWER(p.PRODUCT_NAME) LIKE LOWER('%'||#{searchQuery}||'%') )
AND s.SALE_AT >= #{startDate}
AND s.SALE_AT <= #{endDate}
ORDER BY s.SALE_AT DESC
